        var news = [];
        var urls = []

        for(var i = 0; i < 5; i++) {
            var obj = this.props.chartData[i];
             news = obj.news;
             urls = obj.news_url;

            if(news != undefined) {
                for (var j =0 ; j<2; j++) {
                    if(news[j] != null) {
                        people.push({
                            news: news[j],
                            url: urls[j]
                        });
                    alert(people)
                    }
                }
            }
        }
        this.setState({
            people : people})
        
        }
    onSubmit = async () => {
      await this.Processing();
    };
    
