import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import App from '../App';


class News extends React.Component {
    
    state = {
      newsData: {},
      url: [],
      title: []
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){

        
        super(props);
        
        this.state = {
       newsData :{}
                    }
    }

    componentDidMount() { 
        this.setState({})
    }

    Processing =  () => {

        var title = [];
        var url = [];
        var news = [];
        var urls = []


        /*for(var i = 0; i < 5; i++) {
            var obj = this.props.contacts[i];
             news = obj.news;
            //var urls = obj.news_url
            console.log(news);

            if(news != undefined) {
                for (var j =0 ; j<2; j++) {
                    if(news[j] != null) {
                        title.push(news[j]);
                        //url.push(urls[j]);
                    }
                }
            }
        }*/
        
        alert(title)
        //alert(urls)

        this.setState({
            title : title})
        
        this.setState({
                url : url})
    }
    
    onSubmit = async () => {
      await this.Processing();
    };
    
    render(){
            this.onSubmit();
            return (
            <div className="news">
                <h1> {this.props.contacts}</h1>

            </div>
        )
    }
}

ReactDOM.render(<News />, document.getElementById("root"));
export default News;
