import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {Bar, Line, Pie, Doughnut} from 'react-chartjs-2';
import App from '../App';
import "./contacts.css";
import {GoogleMap, InfoWindow, Marker, withScriptjs, withGoogleMap} from "react-google-maps"

class News extends React.Component {
    
    state = {
      chartData: {},
      location_chartData: {},
      date_charData: {},
      sentiment_chartData : {},
      WrappedMap: {},
      people : []
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){

        
        super(props);
        
        this.state = {
       chartData :{}
                    }
        this.state = {
            people : [{
                news: "",
                url: ""
            }]
        }           
    }

    componentDidMount() { 
        this.setState({})
    }

    Processing =  () => {

        var people = [];
        var news = [];
        var urls = []
        for(var i = 0; i < 5; i++) {
            var obj = this.props.chartData[i];
             news = obj.news;
             urls = obj.news_url;

            if(news != undefined) {
                for (var j =0 ; j<2; j++) {
                    if(news[j] != null) {
                        people.push({
                            news: news[j],
                            url: urls[j]
                        });
                    }
                }
            }
        }
        this.setState({
            people : people})
        
        }
    onSubmit = async () => {
        if (this.props.children == null){
            
        }
    
        else {
            await this.Processing();
        }
     
    };
    
    render(){
            const temp = this.state.WrappedMap
            return (
            <div className="news">
            {this.onSubmit()}
            {this.state.people.map((person, index) => (
            <div className="card" key={index}>
                <div className = "card-title">
                 <h5 className="title">{person.news}</h5>
               </div>
               <div className = "card-title">
                 <button className="buttonTweet" onClick={()=> window.open(`${person.url}`)} >Go to link</button>
               </div>
            </div>
            ))}
            </div>
        )
    }
}

ReactDOM.render(<News />, document.getElementById("root"));
export default News;
