import React, { Component } from "react";
import Form from "./Form";
import Contacts from "./components/contacts";
import Chart from "./components/Chart";
import "./App.css";


class App extends Component {
  state = {
    searchKey: "",
    fields: {},
    contacts :[
    ]
  };

//http://localhost:8080/list?name=Donald

  componentDidMount() {

  }

  onChange = updatedValue => {
    this.setState({
        searchKey: updatedValue
    });
  };

  //http://twilytics.us-east-2.elasticbeanstalk.com/list?name=donald  
  Processing(data){
    console.log("Processing");
    //Languages : en,fr,hi,ru

    var dict = {
      "en": 0,
      "fr": 0,
      "hi": 0,
      "ru": 0,
      "pl": 0
    };

    for(var i = 0; i < data.length; i++) {
      var obj = data[i];
      if (obj.lang in dict){
      dict[obj.lang] = dict[obj.lang] + 1;
      }
    console.log(dict);}
    return dict;
  }
  onSearch = async(search) => {
    await fetch(`http://twilytics.us-east-2.elasticbeanstalk.com/list?name=${search}`)
    .then(res => res.json())
    .then((data) => {
      this.setState({ contacts: data })
      this.Processing(data);
    })
    .catch(console.log)
      this.setState({
          searchKey: ""
      })
  };

  render() {
    const  {searchKey} = this.state;
    return (
      <div className="App">
        <Form onChange={this.onChange} onSearch={this.onSearch} />
        <Contacts contacts={this.state.contacts} searchKey={searchKey}/>
        <Chart chartData={this.state.contacts} />
      </div>
    );
  }
}

export default App;
