import React, { Component } from 'react';
import {Bar} from 'react-chartjs-2';
import App from '../App';

class Chart extends React.Component {
    state = {
      chartData: "",
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){
        super(props);
        this.state = {
            chartData:
                {
                  labels : ['Chennai', 'Buffalo'],
                  datasets:[
                      {
                          label : 'Popualtion',
                          data:[
                              617569,
                              681045,
                          ],
                      backgroundColor:[
                          'rgba(255,99,132,0.6)',
                          'rgba(255,99,132,0.6)'
                      ]
                      }
                  ]
              }
            }
        }

