    }
    render(){
         return (
            <div className="chart">
                  <Bar
                      data={this.props.chartData}
                      width={100}
                      height={50}
                      options={{
                          title:{
                              display:this.props.displayTitle,
                              text:'Largest Cities',
                              fontSize:25
                          },
                      lengend:{
                          display:this.props.displayLegend,
                          positon:this.props.lengendPosition
                      }
                      }}
                      />
              </div>
         )
    }
}

export default Chart;
