import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {Bar, Line, Pie} from 'react-chartjs-2';
import App from '../App';
//import Button from React;

class Chart extends React.Component {
    state = {
      chartData: {},
      location_chartData: {}
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){
        super(props);
        this.state = {
       chartData :{}
                        }
        }

componentDidMount() { 
   this.setState({})
  
}

    Processing =  () => {
        //Languages : en,fr,hi,ru
    
        var lang_dict = {
          "en": 0,
          "fr": 0,
          "hi": 0,
          "ru": 0,
          "pl": 0
        };

        var loc_dict = {
            "US": 3,
            "India": 1,
            "Russia": 4
        };
    
        for(var i = 0; i < this.props.chartData.length; i++) {
          var obj = this.props.chartData[i];
          alert(obj.lang)
          if (obj.lang in lang_dict){
            lang_dict[obj.lang] = lang_dict[obj.lang] + 1;
          }
          alert(obj.created_at)
          if (obj.location in loc_dict){
            loc_dict[obj.location] = loc_dict[obj.location] + 1;
        }
        }

    var lang_keys = []
    var lang_values = []
    var loc_keys = []
    var loc_values = []
    for(var key in lang_dict)  { 
        lang_keys.push( key );
        lang_values.push(lang_dict[key]);
    }
    for(var key in loc_dict)  { 
        loc_keys.push( key );
        loc_values.push(loc_dict[key]);
    }

    this.setState({
        chartData : {
        labels : lang_keys,
        datasets:[
            {
                label : 'Language',
                data: lang_values,
                backgroundColor: ['#a8e0ff', '#8ee3f5', '#70cad1', '#3e517a', '#b08ea2', '#BBB6DF']
            
            }
        ]
    }

})

this.setState({
    location_chartData : {
    labels : loc_keys,
    datasets:[
        {
            label : 'Location',
            data: loc_values,
            backgroundColor: ['#a8e0ff', '#8ee3f5', '#70cad1', '#3e517a', '#b08ea2', '#BBB6DF']
        }
    ]
}

})
    }
    

    onSubmit = async () => {

      await this.Processing();

      
         
      };

    render(){
        
                return (
            
            <div className="chart">
            
            <button className="buttonClass" onClick={ this.onSubmit}>Analyse</button>
            <Bar
                      data={this.state.chartData}
                      width= {20}
                      height={10}
                      postion="left"
                      options={{
                          title:{
                              display:this.props.displayTitle,
                              text:'Language',
                              fontSize:20
                          },
                      lengend:{
                          display:this.props.displayLegend,
                          positon:this.props.lengendPosition
                      },
                      animationEnabled : true,
                      theme: "dark2"
                      }}
                      />
            <Pie
                      data={this.state.location_chartData}
                      width={10}
                      height={10}
                      postion="right"
                      options={{
                          title:{
                              display:this.props.displayTitle,
                              text:'Location',
                              fontSize:20
                          },
                      lengend:{
                          display:this.props.displayLegend,
                          positon:this.props.lengendPosition
                      },
                      animationEnabled : true,
                      theme: "dark1"
                      }}
                      />
            </div>
         )
    }
}

ReactDOM.render(<Chart />, document.getElementById("root"));
export default Chart;
