import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {Bar, Line, Pie, Doughnut} from 'react-chartjs-2';
import App from '../App';
import "./Chart.css";
import {GoogleMap, InfoWindow, Marker, withScriptjs, withGoogleMap} from "react-google-maps"

class News extends React.Component {
    
    state = {
      chartData: {},
      location_chartData: {},
      date_charData: {},
      sentiment_chartData : {},
      WrappedMap: {},
      url: [],
      title: []
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){

        
        super(props);
        
        this.state = {
       chartData :{}
                    }
    }

    componentDidMount() { 
        this.setState({})
    }

    Processing =  () => {

        var title = [];
        var url = [];
        var news = [];
        var urls = []


        for(var i = 0; i < 5; i++) {
            var obj = this.props.chartData[i];
             news = obj.news;
             urls = obj.news_url;

            if(news != undefined) {
                for (var j =0 ; j<2; j++) {
                    if(news[j] != null) {
                        title.push(news[j]);
                        url.push(urls[j]);
                    }
                }
            }
        }
        
        //alert(title)
        //alert(urls)

        this.setState({
            title : title})
        
        this.setState({
                url : url})
        
        var lang_dict = {};

        var loc_dict = {};

        var sentiment_dict = {
            "positive": 0,
            "negative": 0,
            "neutral": 1,
        };

        var date_dict = {};
    
        for(var i = 0; i < this.props.chartData.length; i++) {
            var obj = this.props.chartData[i];

            if (obj.lang in lang_dict){
                lang_dict[obj.lang] = lang_dict[obj.lang] + 1;
            }
            else {
                lang_dict[obj.lang] = 1;
            }

            if (obj["state"] in loc_dict) {
                loc_dict[obj["state"]] = loc_dict[obj["state"]] + 1;
                }
            else {
                    loc_dict[obj["state"]] = 1;
                }
            
            if (obj.sentiment in sentiment_dict) {
             sentiment_dict[obj.sentiment] = sentiment_dict[obj.sentiment] + 1;
            }




        }   

    }
    
    onSubmit = async () => {
      await this.Processing();
    };
    
    render(){
            const temp = this.state.WrappedMap
            return (
            <div className="news">
            <button className="buttonClass" onClick={ this.onSubmit}>News</button> 
            <h5>{this.state.title}</h5>
            <h4>{this.state.url}</h4>

            </div>
        )
    }
}

ReactDOM.render(<News />, document.getElementById("root"));
export default News;
