import React from "react";

export default class Form extends React.Component {
  state = {
    search: ""
  };

  change = e => {
    this.props.onChange({ [e.target.name]: e.target.value });
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  onSubmit = e => {
    e.preventDefault();
    // this.props.onSubmit(this.state);
    this.setState({
      search: ""
    });
    this.props.onChange({
      search: ""
    });
    // Add logic to convert query to URL for backend
    //this.props.onSearch({
    //  query: "Elections"
    //})
    fetch('http://localhost:8080/list?name=Elections')
    .then(res => res.json())
    .then((data) => {
      this.setState({ contacts: data })
    })
    .catch(console.log)
  };

  render() {
    return (
      <form>
        <input
          name="search"
          type="search"
          placeholder="Search"
          value={this.state.search}
          onChange={e => this.change(e)}
        />
        <br />
        <button onClick={e => this.onSubmit(e)}>Submit</button>
      </form>
    );
  }
}
