import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {Bar} from 'react-chartjs-2';
import App from '../App';
//import Button from React;

class Chart extends React.Component {
    state = {
      chartData: {},
      processed_chartData: {}
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){
        super(props);
        this.state = {
       chartData :{}
                        }
        }

componentDidMount() { 
   this.setState({
   chartData:
                {
                  labels : ['Chennai', 'Buffalo'],
                  datasets:[
                      {
                          label : 'Popualtion',
                          data:[
                              617569,
                              681045,
                          ],
                      backgroundColor:[
                          'rgba(255,99,132,0.6)',
                          'rgba(255,99,132,0.6)'
                      ]
                      }
                  ]
              }

})
}

    Processing = async () => {
        //Languages : en,fr,hi,ru
    
        var dict = {
          "en": 0,
          "fr": 0,
          "hi": 0,
          "ru": 0,
          "pl": 0
        };
    
        for(var i = 0; i < this.props.chartData.length; i++) {
          var obj = this.props.chartData[i];
          if (obj.lang in dict){
          dict[obj.lang] = dict[obj.lang] + 1;
          }
    }
    var keys = []
    var values = []
    for(var key in dict)  { 
        keys.push( key );
        values.push(dict[key]);
    }

    var language_data = {
        chartData : {
        labels : keys,
        datasets:[
            {
                label : 'Language',
                data: values,
            backgroundColor:[
                'rgba(255,99,132,0.6)',
                'rgba(200,99,132,0.6)',
                'rgba(195,99,132,0.6)',
                'rgba(160,99,132,0.6)',
                'rgba(150,99,132,0.6)'
            ]
            }
        ]
    }

}
    alert(values);
    alert(data[values]);
    console.log(language_data)
    this.setState({processed_chartData : language_data})
    }
    

    onSubmit = async () => {

      alert("Before processing()")
      await this.Processing();

      
         
      };

    render(){
        
                return (
            
            <div className="chart">
            <button className="buttonClass" onClick={ this.onSubmit}>Analyse</button>
            <Bar
                      data={this.state.processed_chartData}
                      width={50}
                      height={50}
                      options={{
                          title:{
                              display:this.props.displayTitle,
                              text:'Language',
                              fontSize:25
                          },
                      lengend:{
                          display:this.props.displayLegend,
                          positon:this.props.lengendPosition
                      }
                      }}
                      />
            </div>
         )
    }
}

ReactDOM.render(<Chart />, document.getElementById("root"));
export default Chart;
