import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {Bar, Line, Pie, Doughnut} from 'react-chartjs-2';
import App from '../App';
import "./Chart.css";
import {GoogleMap, InfoWindow, Marker, withScriptjs, withGoogleMap} from "react-google-maps"



class Chart extends React.Component {
    
    state = {
      chartData: {},
      url: [],
      title: []
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){

        
        super(props);
        
        this.state = {
       chartData :{}
                    }
    }

    componentDidMount() { 
        this.setState({})
    }

    Processing =  () => {

        var title = [];
        var url = [];
        var news = [];
        var urls = []


        for(var i = 0; i < 5; i++) {
            var obj = this.props.chartData[i];
             news = obj.news;
            //var urls = obj.news_url
            console.log(news);

            if(news != undefined) {
                for (var j =0 ; j<2; j++) {
                    if(news[j] != null) {
                        title.push(news[j]);
                        //url.push(urls[j]);
                    }
                }
            }
        }
        
        alert(title)
        //alert(urls)

        this.setState({
            title : title})
        
        this.setState({
                url : url})
    }
    
    onSubmit = async () => {
      await this.Processing();
    };
    
    render(){
            th
            return (
            <div className="chart">
            <button className="buttonClass" onClick={ this.onSubmit}>Analyse</button> 
            <Doughnut
                    data={this.state.chartData}
                    width= {40}
                    height= {10}
                    postion="left"
                    options={{
                        title:{
                            display:this.props.displayTitle,
                            text:'Language Based Analysis',
                            fontSize:20
                        },
                        lengend:{
                            display:this.props.displayLegend,
                            positon:this.props.lengendPosition
                        },
                    animationEnabled : true,
                    theme: "dark2"
                    }}
            />
            <Pie
                    data={this.state.location_chartData}
                    width={40}
                    height={10}
                    postion="left"
                    options={{
                        title:{
                            display:this.props.displayTitle,
                            text:'Location Based Analysis',
                            fontSize:20
                        },
                        lengend:{
                            display:this.props.displayLegend,
                            positon:this.props.lengendPosition
                        },
                        animationEnabled : true,
                        theme: "dark1"
                    }}
            />
            <Bar
                    data={this.state.date_chartData}
                    width= {40}
                    height={20}
                    postion="left"
                    options={{
                        fill:false,
                        title:{
                            display:this.props.displayTitle,
                            text:'Time series(Number of tweets)',
                            fontSize:20
                            },
                        lengend:{
                           display:this.props.displayLegend,
                           positon:this.props.lengendPosition
                        },
                        animationEnabled : true,
                        theme: "light2"
                    }}
            />
            <Pie
                    data={this.state.sentiment_chartData}
                    width= {40}
                    height={10}
                    postion="left"
                    options={{
                        title:{
                            display:this.props.displayTitle,
                            text:'Sentiment Analysis',
                            fontSize:20
                        },
                        lengend:{
                            display:this.props.displayLegend,
                            positon:this.props.lengendPosition
                        },
                        animationEnabled : true,
                        theme: "dark2"
                    }}
            />
            <div style = {{ width: '100vw', height: '100vh'}}>
            <temp googleMapURL={`http://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyDj1ETylimO4HXNUGtGGvJ1V30kdg-vJ38`}
            loadingElement={<div style={{ height: "100%" }} /> }
            containerElement={<div style={{ height: "100%" }} /> }
            mapElement={<div style={{ height: "100%" }} /> }
           />
          </div>
          <div>
                <h1> {this.state.title}</h1>
                
          </div>
            </div>
        )
    }
}

ReactDOM.render(<Chart />, document.getElementById("root"));
export default Chart;
