import App from '../App';
//import Button from React;

class Chart extends React.Component {
    state = {
      chartData: {},
      location_chartData: {}
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){
        super(props);
        this.state = {
       chartData :{}
                        }
        }

componentDidMount() { 
   this.setState({})
  
}

    Processing =  () => {
        //Languages : en,fr,hi,ru
    
        var lang_dict = {
          "en": 0,
          "fr": 0,
          "hi": 0,
          "ru": 0,
          "pl": 0
        };

        var loc_dict = {
            "US": 0,
            "India": 0,
            "Russia": 0
        };

        var date_dict = {};
    
        for(var i = 0; i < this.props.chartData.length; i++) {
          var obj = this.props.chartData[i];
          if (obj.lang in lang_dict){
            lang_dict[obj.lang] = lang_dict[obj.lang] + 1;
          }
          if (obj["user.location"] in loc_dict){
            loc_dict[obj["user.location"]] = loc_dict[obj["user.location"]] + 1;
        }
        var date = obj["created_at"];
        }

    var lang_keys = []
    var lang_values = []
    var loc_keys = []
    var loc_values = []
    for(var key in lang_dict)  { 
        lang_keys.push( key );
        lang_values.push(lang_dict[key]);
    }
    for(var key in loc_dict)  { 
        loc_keys.push( key );
        loc_values.push(loc_dict[key]);
    }

    this.setState({
        chartData : {
        labels : lang_keys,
        datasets:[
            {
                label : 'Language',
                data: lang_values,
                backgroundColor: ['#a8e0ff', '#8ee3f5', '#70cad1', '#3e517a', '#b08ea2', '#BBB6DF']
            
            }
        ]
    }

})

this.setState({
    location_chartData : {
    labels : loc_keys,
    datasets:[
        {
            label : 'Location',
            data: loc_values,
            backgroundColor: ['#a8e0ff', '#8ee3f5', '#70cad1', '#3e517a', '#b08ea2', '#BBB6DF']
        }
    ]
}

})
    }
    

    onSubmit = async () => {

      await this.Processing();

      
         
      };
    
    render(){
        
            return (
            <button className="buttonClass" onClick={ this.onSubmit}>Analyse</button>
         )
    }
}

ReactDOM.render(<Chart />, document.getElementById("root"));
export default Chart;
