import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {Bar, Line, Pie} from 'react-chartjs-2';
import App from '../App';
//import Button from React;

class Chart extends React.Component {
    state = {
      chartData: {},
      processed_chartData: {}
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){
        super(props);
        this.state = {
       chartData :{}
                        }
        }

componentDidMount() { 
   this.setState({})
  
}

    Processing =  () => {
        //Languages : en,fr,hi,ru
    
        var lang_dict = {
          "en": 0,
          "fr": 0,
          "hi": 0,
          "ru": 0,
          "pl": 0
        };

        var dict = {
            "US": 0,
            "India": 0,
            "Russia": 0
        };
    
        for(var i = 0; i < this.props.chartData.length; i++) {
          var obj = this.props.chartData[i];
          if (obj.lang in lang_dict){
            lang_dict[obj.lang] = lang_dict[obj.lang] + 1;
          }
    }
    var lang_keys = []
    var lang_values = []
    var loc_keys = []
    var loc_values = []
    for(var key in dict)  { 
        keys.push( key );
        values.push(dict[key]);
    }

    this.setState({
        chartData : {
        labels : lang_keys,
        datasets:[
            {
                label : 'Language',
                data: lang_values,
                backgroundColor:[
                'rgba(255,99,130,0.6)',
                'rgba(200,99,130,0.6)',
                'rgba(195,99,130,0.6)',
                'rgba(160,99,132,0.6)',
                'rgba(150,99,132,0.6)'
            ]
            }
        ]
    }

})

this.setState({
    chartData : {
    labels : keys,
    datasets:[
        {
            label : 'Location',
            data: values,
            backgroundColor:[
            'rgba(255,99,130,0.6)',
            'rgba(200,99,130,0.6)',
            'rgba(195,99,130,0.6)',
            'rgba(160,99,132,0.6)',
            'rgba(150,99,132,0.6)'
        ]
        }
    ]
}

})
    }
    

    onSubmit = async () => {

      await this.Processing();

      
         
      };

    render(){
        
                return (
            
            <div className="chart">
            <button className="buttonClass" onClick={ this.onSubmit}>Analyse</button>
            <Bar
                      data={this.state.chartData}
                      width={10}
                      height={10}
                      options={{
                          title:{
                              display:this.props.displayTitle,
                              text:'Language',
                              fontSize:20
                          },
                      lengend:{
                          display:this.props.displayLegend,
                          positon:this.props.lengendPosition
                      },
                      animationEnabled : true,
                      theme: "dark2"
                      }}
                      />
            <Pie
                      data={this.state.Location_chartData}
                      width={10}
                      height={10}
                      options={{
                          title:{
                              display:this.props.displayTitle,
                              text:'Location',
                              fontSize:20
                          },
                      lengend:{
                          display:this.props.displayLegend,
                          positon:this.props.lengendPosition
                      },
                      animationEnabled : true,
                      theme: "dark1"
                      }}
                      />
            </div>
         )
    }
}

ReactDOM.render(<Chart />, document.getElementById("root"));
export default Chart;
