import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {Bar, Line, Pie, Doughnut} from 'react-chartjs-2';
import App from '../App';
import "./Chart.css";
import {GoogleMap, InfoWindow, Marker, withScriptjs, withGoogleMap} from "react-google-maps"



class Chart extends React.Component {
    
    state = {
      chartData: {},
      url: [],
      title: []
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){

        
        super(props);
        
        this.state = {
       chartData :{}
                    }
    }

    componentDidMount() { 
        this.setState({})
    }

    Processing =  () => {

        var title = [];
        var url = [];
        var news = [];
        var urls = []


        for(var i = 0; i < 5; i++) {
            var obj = this.props.chartData[i];
             news = obj.news;
            //var urls = obj.news_url
            console.log(news);

            if(news != undefined) {
                for (var j =0 ; j<2; j++) {
                    if(news[j] != null) {
                        title.push(news[j]);
                        //url.push(urls[j]);
                    }
                }
            }
        }
        
        alert(title)
        //alert(urls)

        this.setState({
            title : title})
        
        this.setState({
                url : url})
    }
    
    onSubmit = async () => {
      await this.Processing();
    };
    
    render(){
            this.onSubmit();
            return (
            <div className="chart">
          <div>
                <h1> {this.state.title}</h1>
                
          </div>
            </div>
        )
    }
}

ReactDOM.render(<Chart />, document.getElementById("root"));
export default Chart;
