import React, { Component } from 'react';
import {Bar} from 'react-chartjs-2';
import App from '../App';

class Chart extends React.Component {
    state = {
      chartData: "",
    };

    static defaultProps = {
        displayTitle:true,
        displayLegend:true,
        lengendPosition:'right'
    }

    constructor(props){
        super(props);
        this.state = {
            chartData:
                {
                  labels : ['Chennai', 'Buffalo'],
                  datasets:[
                      {
                          label : 'Popualtion',
                          data:[
                              617569,
                              681045,
                          ],
                      backgroundColor:[
                          'rgba(255,99,132,0.6)',
                          'rgba(255,99,132,0.6)'
                      ]
                      }
                  ]
              }
            }
        }

    Processing(data){
        console.log("Processing");
        //Languages : en,fr,hi,ru
    
        var dict = {
          "en": 0,
          "fr": 0,
          "hi": 0,
          "ru": 0,
          "pl": 0
        };
    
        for(var i = 0; i < data.length; i++) {
          var obj = data[i];
          if (obj.lang in dict){
          dict[obj.lang] = dict[obj.lang] + 1;
          }
        console.log(dict);}

        this.setState(
            this.state = {
                chartData:
                    {
                      labels : dict.keys(),
                      datasets:[
                          {
                              label : 'Language',
                              data: dict.values(),
                          backgroundColor:[
                              'rgba(255,99,132,0.6)',
                              'rgba(255,99,132,0.6)'
                          ]
                          }
                      ]
                  }
                })
        return dict;
    }
    
    render(){
        this.Processing(this.props.chartData)
        console.log("Print");
         return (
            <div className="chart">
                  <Bar
                      data={this.props.chartData}
                      width={100}
                      height={50}
                      options={{
                          title:{
                              display:this.props.displayTitle,
                              text:'Largest Cities',
                              fontSize:25
                          },
                      lengend:{
                          display:this.props.displayLegend,
                          positon:this.props.lengendPosition
                      }
                      }}
                      />
              </div>
         )
    }
}

export default Chart;
